import { Box, Stack} from "@material-ui/core";
import { Document, Page } from 'react-pdf/dist/esm/entry.webpack';
import {useState, useEffect} from 'react';
import resume from "../images/resumeTemp.pdf";


const onWindowChange = 

function Resume() {
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);
    const [documentSize, setDocumentSize] = useState(window.innerWidth >=950? 900 : window);

    const onDocumentLoadSuccess = ({numPages}) => {
        setNumPages(numPages);
    };

    window.addEventListener('resize', ()=>{
        window.innerWidth >= 950? setDocumentSize(900) : setDocumentSize(window.innerWidth * (0.9))
    })

    return (
        <Box className='box' component={Stack} alignItems='center' backgroundColor='red' p={1}  overflow='auto' 
        sx={{'& *': {borderRadius:'0.7rem'}}}>
                <Document 
                file={resume}
                onLoadSuccess={onDocumentLoadSuccess}>
                <Page renderMode='svg'   width={documentSize} pageNumber={pageNumber}  />
                </Document>

        </Box>
    )
}


export default Resume;
