import { AppBar, Avatar, Badge, Button, Grid, Icon, IconButton, InputAdornment, InputBase, Menu, MenuItem, Paper, Toolbar, Typography } from "@material-ui/core";
import { Link, useLocation } from 'react-router-dom';
import {useState} from 'react';
import { Box } from "@material-ui/system";
import wsb from '../images/wsb.png';
import generator from "element-id-generator";
import { Stack } from "@material-ui/core";
import { indigo } from '@material-ui/core/colors';
import {Paths} from "./utils";
import AccountCircle from '@material-ui/icons/AccountCircle';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

const tabList = [
    { icon: 'user', title: 'AboutMe', badgeCount: 0, link: Paths.aboutMe },
    { icon: 'school', title: 'Education', badgeCount: 4, link: Paths.education },
    { icon: 'network-wired', title: 'Projects', badgeCount: 1, link: Paths.projects },
    { icon: 'code', title: 'Skills', badgeCount: '15', link: Paths.skills },
    { icon: 'book', title: 'Courses', badgeCount: 4, link: Paths.courses },
    { icon: 'briefcase', title: 'Work History', badgeCount: '1', link: Paths.workHistory },
    { icon: 'file', title: 'Resume', badgeCount: '1', link: Paths.resume },
    { icon: 'info', title: 'MetaData', link: Paths.metadata }
]

const defaultButtonStyle = {
    width: 'fit-content',
    backgroundColor: 'white',
    borderRadius: '0.7rem',
    color: indigo[900],
    ':hover': { backgroundColor: indigo[600], color: 'white' }
};
const selectedButtonStyle = {
    width: 'fit-content',
    backgroundColor: indigo[900],
    borderRadius: '0.7rem',
    color: 'white',
    ':hover': { backgroundColor: indigo[600], color: 'white' }
};
const TabItem = ({ data }) => {
    const location = useLocation();
    return (
        <Grid item>
            <Link to={data.link} style={{ textDecoration: 'none' }}>
                <Paper type='button' sx={location.pathname === data.link ? selectedButtonStyle : defaultButtonStyle}>
                    <Stack alignItems='center' justifyContent='center' sx={{ pt: '1rem', pb: '0.5rem', px: '1.2rem' }}>
                        <Badge badgeContent={data.badgeCount} color='error'>
                            <Icon baseClassName="fas" className={`fa-${data.icon}`} sx={{ fontSize: 'large', width: 'fit-content' }} />
                        </Badge>
                        <Typography>{data.title}</Typography>
                    </Stack>
                </Paper>
            </Link>
        </Grid>
    )
};

const Admin = ({anchor, open, close}) => {
    return (
    <Menu 
    anchorEl={anchor} open={open} onClose={close}>
        <MenuItem disabled>Restricted</MenuItem>
    </Menu>
    )
}

function Nav() {
    const [adminAnchor, setAdminAnchor] = useState(null);
    const adminOpen = Boolean(adminAnchor);
    const onAdminClose = () => setAdminAnchor(null);
    const onAdminOpen = (event) => setAdminAnchor(event.currentTarget);
    return (
        <>
            <AppBar color='transparent' position='static' elevation='0'>
                <Grid container columnSpacing={1}>
                    {/* nav bar */}
                    <Grid item xs={12}>
                        <Toolbar direction='row' spacing={1} justifyContent='space-between' component={Stack} py='0.2rem'
                            sx={{ backgroundImage: 'linear-gradient(180deg,  rgb(26, 35, 126),rgb(57, 73, 171),rgb(26, 35, 126))' }}>

                            <Stack direction='row' alignItems='center' spacing={0.5} >
                                <Avatar src={wsb} sx={{ mr: '0.2rem' }} sx={{ display: 'inline-block' }} />
                                <Typography className='colorTrans' sx={{ fontSize: {xs:'1.1rem',sm:'1.5rem'}, fontWeight: '500', display: 'inline-block' }}>
                                    Full Stack Developer
                                </Typography>
                            </Stack>

                            <Stack direction='row' alignItems='center'>
                                <AccountCircle sx={{ color: "white" }} />
                                <IconButton onClick={onAdminOpen}>
                                    <Typography color='white' fontSize='1.2rem' fontWeight='500'>Admin 
                                    </Typography>
                                    <KeyboardArrowDownIcon sx={{color:'white'}} />
                                </IconButton>
                                <Admin anchor={adminAnchor} close={onAdminClose} open={adminOpen}/>
                            </Stack>

                        </Toolbar>
                    </Grid>

                    {/* tab bar  */}
                    <Grid item container sx={{ p: '1rem' }} xs={12}
                        justifyContent='center' alignItems='center' spacing={2}>
                        {tabList.map(item => <TabItem key={generator.generate()} data={item} />)}
                    </Grid>

                </Grid>
            </AppBar>
        </>
    )
}

export default Nav;
