import { Box, Chip, Grid, Icon, Paper, Stack, Typography } from "@material-ui/core";
import { indigo, pink, teal, red, green, deepOrange, deepPurple, purple } from '@material-ui/core/colors';
import LeaderShip from "../images/leadership.svg";
import Collaboration from "../images/support.svg";
import CustomerService from "../images/customer-service.svg";
import ProblemSolving from "../images/puzzle.svg";
import CriticalThinking from "../images/thinking.svg";
import Creativity from "../images/design-thinking.svg";
import VisualThinking from "../images/storyboard.svg";
import WorkEthic from "../images/struggle.svg";
import Enthusiasm from "../images/business.svg";
import Organization from "../images/project-plan.svg";


export const softData = [
    { item: "Leadership", icon:LeaderShip},
    { item: "Collaboration", icon:Collaboration},
    { item: "Customer Service", icon:CustomerService },
    { item: "Problem Solving", icon: ProblemSolving},
    { item: "Critical Thinking", icon:CriticalThinking},
    { item: "Creativity", icon:Creativity},
    { item: "Visual Thinking", icon:VisualThinking},
    { item: "Work Ethic", icon:WorkEthic},
    { item: "Enthusiasm", icon:Enthusiasm},
    { item: "Organization", icon:Organization},
];


export const hardData = [
    {
        category: "Languages", sx: { background: `linear-gradient(to bottom, ${teal[900]}, ${teal[700]}, ${teal[900]})` }, data: [
            { item: "Python", color: 'bg-black text-white', icon: "devicon-python-plain" },
            { item: "HTML", color: 'bg-black text-white', icon: "devicon-html5-plain" },
            { item: "JavaScript", color: 'bg-black text-white', icon: "devicon-javascript-plain" },
            { item: "CSS", color: 'bg-black text-white', icon: "devicon-css3-plain" },
            { item: "C", color: 'bg-black text-white', icon: "devicon-c-plain" },
            { item: "java", color: 'bg-black text-white', icon: "devicon-java-plain" },
            { item: "kotlin", color: 'bg-black text-white', icon: "devicon-kotlin-plain" },
            { item: "Ruby", color: 'bg-black text-white', icon: "devicon-ruby-plain" },
            { item: "Rust", color: 'bg-black text-white', icon: "devicon-rust-plain" },
            { item: "MatLab", color: 'bg-black text-white', icon: "devicon-matlab-plain" },
            { item: "Ocaml", color: 'bg-black text-white', icon: "devicon-ocaml-plain" },
            {
                item: "Assembly", color: 'bg-black text-white',
                iconName: "AS"
            },
        ]
    },

    {
        category: "Frameworks", sx: { background: `linear-gradient(to bottom, ${indigo[900]}, ${indigo[700]}, ${indigo[900]})` }, data: [
            { item: "Express", color: 'bg-black text-white', icon: "devicon-express-original" },
            { item: "Bootstrap", color: 'bg-black text-white', icon: "devicon-bootstrap-plain" },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: "devicon-tailwindcss-plain" },
            { item: "Material UI", color: 'bg-black text-white', icon: "devicon-materialui-plain" },
        ]
    },
    {
        category: "Libraries", sx: { background: `linear-gradient(to bottom, ${red[900]}, ${red[700]}, ${red[900]})` }, data: [
            { item: "React", color: 'bg-black text-white', icon: "devicon-react-original" },
            { item: "NPM", color: 'bg-black text-white', icon: "devicon-npm-original-wordmark" },
        ]
    },
    {
        category: "Runtime Environments", sx: { background: `linear-gradient(to bottom, ${green[900]}, ${green[700]}, ${green[900]})` }, data: [
            { item: "Node.js", color: 'bg-black text-white', icon: "devicon-nodejs-plain" },
            { item: "JQuery", color: 'bg-black text-white', icon: "devicon-jquery-plain" },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: "devicon-tailwindcss-plain" },
            { item: "Material UI", color: 'bg-black text-white', icon: "devicon-materialui-plain" },
        ]
    },
    {
        category: "IDEs", sx: { background: `linear-gradient(to bottom, ${deepOrange[900]}, ${deepOrange[700]}, ${deepOrange[900]})` }, data: [
            { item: "Jupyter", color: 'bg-black text-white', icon: "devicon-jupyter-plain" },
            { item: "VSCode", color: 'bg-black text-white', icon: "devicon-vscode-plain" },
            { item: "PyCharm", color: 'bg-black text-white', icon: "devicon-pycharm-plain" },
            { item: "IntelliJ", color: 'bg-black text-white', icon: "devicon-intellij-plain" },
            { item: "Android Studio", color: 'bg-black text-white', icon: "devicon-android-plain" },
        ]
    },
    {
        category: "Database Language & Frameworks", sx: { background: `linear-gradient(to bottom, ${deepPurple[900]}, ${deepPurple[700]}, ${deepPurple[900]})` }, data: [
            { item: "mongodb", color: 'bg-black text-white', icon: "devicon-mongodb-plain" },
            { item: "MySql", color: 'bg-black text-white', icon: "devicon-mysql-plain" },
            { item: "PostgreSql", color: 'bg-black text-white', icon: "devicon-postgresql-plain" },
            { item: "firebase", color: 'bg-black text-white', icon: "devicon-firebase-plain" },
        ]
    }
];


function Skills() {
    return (
        <Box display='flex' justifyContent='center' overflow='auto' width='100%' backgroundColor='blu' p={1}>
            <Box component={Grid} container width='60rem' backgroundColor='purpl' spacing={3} flexWrap='wrap'>
                <Grid item width={{ xs: 1, sm: 5 / 8 }}>
                    <Box sx={{ backgroundColor: 'transparent' }}>
                        <Typography mx='auto' component={Paper} fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' borderRadius='0.5rem'
                            p={0.4} color={indigo[900]} mb={2}>
                            Hard Skills
                        </Typography>

                        <Stack spacing={1}>
                            {/* Categories  */}
                            {hardData.map(data =>

                                <Stack alignItems='center'>
                                    <Paper sx={Object.assign(data.sx || {}, { borderRadius: '1rem', px: 1, py: 0.4, width: 'fit-content' })} >
                                        <Typography textAlign='center' color='white' fontSize='1.2rem' fontWeight='600'>{data.category}</Typography>
                                        <Grid container spacing={1} p={1} flexWrap='wrap' justifyContent='center'>
                                            {data.data.map(item =>
                                                // items 
                                                <Grid item>
                                                    <Chip item color='primary' sx={{
                                                        fontSize: '1.2rem', color: 'white', borderColor: 'black', backgroundColor: 'black',
                                                        borderRadius: '0.3rem'
                                                    }}
                                                        icon={<Icon className={item.icon}
                                                            sx={item.iconName ? { width: 'fit-content', fontSize: '1.2rem', fontWeight: 600, mb: '0.4rem' } : {}}>{item.iconName}</Icon>}
                                                        label={item.item} variant='outlined' />
                                                </Grid>
                                            )}
                                        </Grid>
                                    </Paper>
                                </Stack>

                            )}
                        </Stack>
                    </Box>
                </Grid>
                <Grid item width={{ xs: 1, sm: 3 / 8 }} >
                    <Typography component={Paper} mx='auto' fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' 
                    mb={3} borderRadius='0.5rem' p={0.4} color={purple[700]}>
                        Soft Skills
                    </Typography>
                    <Box component={Grid} container justifyContent='center' columnSpacing={0.5} rowSpacing={1} flexWrap='wrap'
                        borderRadius='1rem'  py={3} sx={{ background: `linear-gradient(to bottom, ${teal[900]}, ${teal[700]}, ${teal[900]})` }}>
                        {softData.map(data =>
                            <Grid item>
                                <Chip item color='primary' sx={{
                                    fontSize: '1.2rem', color: 'white', borderColor: 'black', backgroundColor: 'black',
                                    borderRadius: '0.3rem', width: 'fit-content'
                                }}
                                    icon={<Icon component='img' src={data.icon} />}
                                    label={data.item} variant='outlined' />
                            </Grid>
                        )}
                    </Box>

                </Grid>
            </Box>
        </Box>
    )
}

export default Skills;
