import { Box, Chip, Grid, Icon, Paper, Stack, Typography } from "@material-ui/core";
import { indigo, pink, teal, red, green} from '@material-ui/core/colors';

export const softData = [
    { item: "Leadership", color: 'bg-black' },
    { item: "Collaboration", color: 'bg-black' },
    { item: "Customer Service", color: 'bg-black' },
    { item: "Problem Solving", color: 'bg-black' },
    { item: "Critical Thinking", color: 'bg-black' },
    { item: "Creativity", color: 'bg-black' },
    { item: "Visual Thinking", color: 'bg-black' },
    { item: "Work Ethic", color: 'bg-black' },
    { item: "Enthusiasm", color: 'bg-black' },
    { item: "Organization", color: 'bg-black' },
];


export const hardData = [
    {
        category: "Languages", sx: { background: `linear-gradient(to bottom, ${teal[900]}, ${teal[700]}, ${teal[900]})` }, data: [
            { item: "Python", color: 'bg-black text-white', icon: "devicon-python-plain"},
            { item: "HTML", color: 'bg-black text-white', icon: "devicon-html5-plain"},
            { item: "JavaScript", color: 'bg-black text-white', icon: "devicon-javascript-plain" },
            { item: "CSS", color: 'bg-black text-white', icon: "devicon-css3-plain" },
            { item: "C", color: 'bg-black text-white', icon: "devicon-c-plain"},
            { item: "java", color: 'bg-black text-white', icon: "devicon-java-plain" },
            { item: "kotlin", color: 'bg-black text-white', icon: "devicon-kotlin-plain" },
            { item: "Ruby", color: 'bg-black text-white', icon: "devicon-ruby-plain" },
            { item: "Rust", color: 'bg-black text-white', icon: "devicon-rust-plain" },
            { item: "MatLab", color: 'bg-black text-white', icon: "devicon-matlab-plain" },
            { item: "Ocaml", color: 'bg-black text-white', icon: "devicon-ocaml-plain" },
            {
                item: "Assembly", color: 'bg-black text-white',
                iconName: "AS"
            },
        ]
    },

    {
        category: "Frameworks", sx: { background: `linear-gradient(to bottom, ${indigo[900]}, ${indigo[700]}, ${indigo[900]})` }, data: [
            { item: "Express", color: 'bg-black text-white', icon: "devicon-express-original" },
            { item: "Bootstrap", color: 'bg-black text-white', icon: "devicon-bootstrap-plain" },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: "devicon-tailwindcss-plain" },
            { item: "Material UI", color: 'bg-black text-white', icon: "devicon-materialui-plain" },
        ]
    },
    {
        category: "Libraries", sx: { background: `linear-gradient(to bottom, ${red[900]}, ${red[700]}, ${red[900]})` }, data: [
            { item: "React", color: 'bg-black text-white', icon: "devicon-react-original"},
            { item: "NPM", color: 'bg-black text-white', icon: "devicon-npm-original-wordmark" },
        ]
    },
    {
        category: "Runtime Environments",  sx: { background: `linear-gradient(to bottom, ${green[900]}, ${green[700]}, ${green[900]})` }, data: [
            { item: "Node.js", color: 'bg-black text-white', icon:"devicon-nodejs-plain" },
            { item: "JQuery", color: 'bg-black text-white', icon: <i class="devicon-jquery-plain text-2xl mr-2" /> },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: <i class="devicon-tailwindcss-plain text-2xl mr-2" /> },
            { item: "Material UI", color: 'bg-black text-white', icon: <i class="devicon-materialui-plain text-2xl mr-2" /> },
        ]
    },
    {
        category: "IDEs", color: 'bg-gradient-to-t  from-yellow-900 via-yellow-700 to-yellow-900', data: [
            { item: "Jupyter", color: 'bg-black text-white', icon: <i class="devicon-jupyter-plain text-2xl mr-2" /> },
            { item: "VSCode", color: 'bg-black text-white', icon: <i class="devicon-vscode-plain text-2xl mr-2" /> },
            { item: "PyCharm", color: 'bg-black text-white', icon: <i class="devicon-pycharm-plain text-2xl mr-2" /> },
            { item: "IntelliJ", color: 'bg-black text-white', icon: <i class="devicon-intellij-plain text-2xl mr-2" /> },
            { item: "Android Studio", color: 'bg-black text-white', icon: <i class="devicon-android-plain text-2xl mr-2" /> },
        ]
    },
    {
        category: "Database Language & Frameworks", color: 'bg-gradient-to-t  from-red-900 via-red-700 to-red-900', data: [
            { item: "mongodb", color: 'bg-black text-white', icon: <i class="devicon-mongodb-plain text-2xl mr-2" /> },
            { item: "MySql", color: 'bg-black text-white', icon: <i class="devicon-mysql-plain text-2xl mr-2" /> },
            { item: "PostgreSql", color: 'bg-black text-white', icon: <i class="devicon-postgresql-plain text-2xl mr-2" /> },
            { item: "firebase", color: 'bg-black text-white', icon: <i class="devicon-firebase-plain text-2xl mr-2" /> },
        ]
    }
];


function Skills() {
    return (
        <Box display='flex' justifyContent='center' overflow='auto' width='100%' backgroundColor='blue'> 
            <Box component={Grid} container  width='60rem' backgroundColor='purple' spacing={1}>
                <Grid item width={5 / 8}>
                    <Paper sx={{ backgroundColor: 'transparent' }}>
                        <Typography mx='auto' fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' borderRadius='0.5rem'
                            p={0.4} color={indigo[900]} mb={2}>
                            Hard Skills
                        </Typography>

                        <Stack  spacing={1}>
                            {hardData.map(data =>

                                <Stack   alignItems='center'>
                                    <Paper sx={Object.assign(data.sx || {}, { borderRadius: '1rem', px: 1, py: 0.4, width:'fit-content' })} >
                                        <Typography textAlign='center' color='white' fontSize='1.2rem' fontWeight='600'>{data.category}</Typography>
                                        <Grid container spacing={1} p={1} flexWrap='wrap' justifyContent='center'>
                                            {data.data.map(item =>
                                                <Grid item>
                                                    <Chip item color='primary' sx={{ fontSize: '1.2rem', color: 'white', borderColor: 'black', backgroundColor: 'black',
                                                     borderRadius: '0.3rem' }}
                                                        icon={<Icon className={item.icon} 
                                                        sx={item.iconName? {width:'fit-content', fontSize:'1.2rem', fontWeight:600 ,mb:'0.4rem'} : {}}>{item.iconName}</Icon>}
                                                        label={item.item} variant='outlined' />
                                                </Grid>
                                            )}
                                        </Grid>
                                    </Paper>
                                </Stack>

                            )}
                        </Stack>
                    </Paper>
                </Grid>
                <Grid item width={3 / 8}>
                    <Typography mx='auto' fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' borderRadius='0.5rem' p={1} color={pink[900]}>
                        Soft Skills
                    </Typography>
                </Grid>
            </Box>
        </Box>
    )
}

export default Skills;
