import { AppBar, Avatar, Badge, Button, Grid, Icon, InputAdornment, InputBase, Paper, Toolbar, Typography } from "@material-ui/core";
import { Link, useLocation } from 'react-router-dom';
import { Box } from "@material-ui/system";
import wsb from '../images/wsb.png';
import generator from "element-id-generator";
import { Stack } from "@material-ui/core";
import { indigo, g} from '@material-ui/core/colors';
import Paths from "./paths";

const tabList = [
    { icon: 'user', title: 'AboutMe', badgeCount: 0, link: Paths.aboutMe },
    { icon: 'school', title: 'Education', badgeCount: 4, link: Paths.education },
    { icon: 'network-wired', title: 'Projects', badgeCount: 1, link: Paths.projects },
    { icon: 'code', title: 'Skills', badgeCount: '15', link: Paths.skills },
    { icon: 'book', title: 'Courses', link: Paths.courses },
    { icon: 'briefcase', title: 'Work History', badgeCount: '1', link: Paths.workHistory },
    { icon: 'file', title: 'Resume', badgeCount: '1', link: Paths.resume },
    { icon: 'info', title: 'MetaData', link: Paths.metadata }
]

const defaultButtonStyle = {
    width: 'fit-content',
    backgroundColor: 'white',
    borderRadius: '0.7rem',
    color: indigo[900],
    ':hover': { backgroundColor: green[900], color: 'white' }
};
const selectedButtonStyle = {
    width: 'fit-content',
    backgroundColor: indigo[900],
    borderRadius: '0.7rem',
    color: 'white',
    ':hover': { backgroundColor: green[900], color: 'white' }
};
const TabItem = ({ data }) => {
    const location = useLocation();
    return (
        <Grid item>
            <Link to={data.link} style={{ textDecoration: 'none' }}>
                <Paper type='button' sx={location.pathname === data.link? selectedButtonStyle : defaultButtonStyle}>
                    <Stack alignItems='center' justifyContent='center' sx={{ pt: '1rem', pb: '0.5rem', px: '1.2rem' }}>
                        <Badge badgeContent={data.badgeCount} color='error'>
                            <Icon baseClassName="fas" className={`fa-${data.icon}`} sx={{ fontSize: 'large', width: 'fit-content' }} />
                        </Badge>
                        <Typography>{data.title}</Typography>
                    </Stack>
                </Paper>
            </Link>
        </Grid>
    )
}

function Nav() {
    return (
        <>
            <AppBar color='transparent' position='static' elevation='0'>
                <Grid container columnSpacing={1} >
                    {/* nav bar */}
                    <Grid item xs={12}>
                        <Toolbar direction={{ xs: 'column', sm: 'row' }} spacing={1} justifyContent='space-between' component={Stack} py='0.2rem'
                            sx={{ backgroundImage: 'linear-gradient(180deg,  rgb(26, 35, 126),rgb(57, 73, 171),rgb(26, 35, 126))' }}>
                            <Stack direction='row' alignItems='center' spacing={0.5} alignSelf={{ xs: 'flex-start', sm: 'center' }}>
                                <Avatar src={wsb} sx={{ mr: '0.2rem' }} sx={{ display: 'inline-block' }} />
                                <Typography className='colorTrans' sx={{ fontSize: '1.5rem', fontWeight: '500', display: 'inline-block' }}>
                                    Full Stack Developer
                                </Typography>
                            </Stack>

                            <Box sx={{ display: 'flex', flexWrap: 'nowrap' }}>
                                <InputBase placeholder='Search' variant="outlined"
                                    sx={{ backgroundColor: 'white', p: '0.2rem', borderRadius: '0.4rem', mr: '0.4rem' }}
                                    startAdornment={
                                        <InputAdornment position="start">
                                            <Icon>search</Icon>
                                        </InputAdornment>
                                    } />
                                <Button variant='contained' color='error'>Search</Button>
                            </Box>
                        </Toolbar>
                    </Grid>

                    {/* tab bar  */}
                    <Grid item container sx={{ p: '1rem' }} xs={12}
                        justifyContent='center' alignItems='center' spacing={2}>
                        {tabList.map(item => <TabItem key={generator.generate()} data={item} />)}
                    </Grid>

                </Grid>
            </AppBar>
        </>
    )
}

export default Nav;
