import { Box, Button, Stack, Typography } from "@material-ui/core";
import { Document, Page } from 'react-pdf/dist/esm/entry.webpack';
import { useState, useEffect } from 'react';
import resume from "../images/resumeTemp.pdf";

const resizeDocument = (setDocumentSize) => {
    window.innerWidth >= 950 ? setDocumentSize(900) : setDocumentSize(window.innerWidth * (0.93))
}


function Resume() {
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);
    const [documentSize, setDocumentSize] = useState(null);

    const onDocumentLoadSuccess = ({ numPages }) => {
        setNumPages(numPages);
    };

    window.addEventListener('resize', () => resizeDocument(setDocumentSize));

    useEffect(() => { resizeDocument(setDocumentSize) })

    return (
        <Box component={Stack} alignItems='center'  spacing={2} backgroundColor='re' p={1} overflow='auto'>
           <Box>
               <Button component='a' variant='contained' size='large' color='success' href={resume} target='_blank' download='resume_Brian_Ojoh_Mile'>Download Resume</Button>
           </Box>
           {Array.from({length:numPages}, (x,i))}
            <Box  sx={{'*': { borderRadius: '0.7rem' } }}>
                <Document
                    file={resume}
                    onLoadSuccess={onDocumentLoadSuccess}>
                    <Page width={documentSize} pageNumber={pageNumber} />
                </Document>

            </Box>
        </Box>
    )
}


export default Resume;
