import { Box, Chip, Grid, Icon, Paper, Stack, Typography} from "@material-ui/core";
import { indigo, pink, teal} from '@material-ui/core/colors';

export const softData = [
    { item: "Leadership", color: 'bg-black' },
    { item: "Collaboration", color: 'bg-black' },
    { item: "Customer Service", color: 'bg-black' },
    { item: "Problem Solving", color: 'bg-black' },
    { item: "Critical Thinking", color: 'bg-black' },
    { item: "Creativity", color: 'bg-black' },
    { item: "Visual Thinking", color: 'bg-black' },
    { item: "Work Ethic", color: 'bg-black' },
    { item: "Enthusiasm", color: 'bg-black' },
    { item: "Organization", color: 'bg-black' },
];


export const hardData = [
    {
        category: "Languages", sx: {background:`linear-gradient(to bottom, ${teal[900]}, ${teal[700]}, ${teal[900]})`},  data: [
            { item: "Python", color: 'bg-black text-white', icon: <i class="devicon-python-plain text-2xl mr-2" /> },
            { item: "HTML", color: 'bg-black text-white', icon: <i class="devicon-html5-plain text-2xl mr-2" /> },
            { item: "JavaScript", color: 'bg-black text-white', icon: <i class="devicon-javascript-plain text-2xl mr-2" /> },
            { item: "CSS", color: 'bg-black text-white', icon: <i class="devicon-css3-plain text-2xl mr-2" /> },
            { item: "C", color: 'bg-black text-white', icon: <i class="devicon-c-plain text-2xl mr-2" /> },
            { item: "java", color: 'bg-black text-white', icon: <i class="devicon-java-plain text-2xl mr-2" /> },
            { item: "kotlin", color: 'bg-black text-white', icon: <i class="devicon-kotlin-plain text-2xl mr-2" /> },
            { item: "Ruby", color: 'bg-black text-white', icon: <i class="devicon-ruby-plain text-2xl mr-2" /> },
            { item: "Rust", color: 'bg-black text-white', icon: <i class="devicon-rust-plain text-2xl mr-2" /> },
            { item: "MatLab", color: 'bg-black text-white', icon: <i class="devicon-matlab-plain text-2xl mr-2" /> },
            { item: "Ocaml", color: 'bg-black text-white', icon: <i class="devicon-ocaml-plain text-2xl mr-2" /> },
            {item: "Assembly", color: 'bg-black text-white',
                icon: <svg height="30" width="46"><text x="0" y="22" fill='white'>ASM</text></svg>},
        ]
    },

    {
        category: "Frameworks", color:'bg-gradient-to-t  from-blue-900 via-blue-700 to-blue-900',  data: [
            { item: "Express", color: 'bg-black text-white', icon: <i class="devicon-express-original text-2xl mr-2"/> },
            { item: "Bootstrap", color: 'bg-black text-white', icon: <i class="devicon-bootstrap-plain text-2xl mr-2"/> },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: <i class="devicon-tailwindcss-plain text-2xl mr-2" /> },
            { item: "Material UI", color: 'bg-black text-white', icon: <i class="devicon-materialui-plain text-2xl mr-2"/> },
        ]
    },
    {
        category: "Libraries", color:'bg-gradient-to-t  from-red-800 via-red-600 to-red-800',  data: [
            { item: "React", color: 'bg-black text-white', icon: <i class="devicon-react-original text-2xl mr-2" /> },
            { item: "NPM", color: 'bg-black text-white', icon: <i class="devicon-npm-original-wordmark text-2xl mr-2"/> },
        ]
    },
    {
        category: "Runtime Environments", color:'bg-gradient-to-t  from-green-800 via-green-600 to-green-800',  data: [
            { item: "Node.js", color: 'bg-black text-white', icon: <i class="devicon-nodejs-plain text-2xl mr-2"/> },
            { item: "JQuery", color: 'bg-black text-white', icon: <i class="devicon-jquery-plain text-2xl mr-2" /> },
            { item: "TailwindCSS", color: 'bg-black text-white', icon: <i class="devicon-tailwindcss-plain text-2xl mr-2" /> },
            { item: "Material UI", color: 'bg-black text-white', icon: <i class="devicon-materialui-plain text-2xl mr-2"/> },
        ]
    },
    {
        category: "IDEs", color:'bg-gradient-to-t  from-yellow-900 via-yellow-700 to-yellow-900',  data: [
            { item: "Jupyter", color: 'bg-black text-white', icon: <i class="devicon-jupyter-plain text-2xl mr-2"/> },
            { item: "VSCode", color: 'bg-black text-white', icon: <i class="devicon-vscode-plain text-2xl mr-2" /> },
            { item: "PyCharm", color: 'bg-black text-white', icon: <i class="devicon-pycharm-plain text-2xl mr-2" /> },
            { item: "IntelliJ", color: 'bg-black text-white', icon: <i class="devicon-intellij-plain text-2xl mr-2" /> },
            { item: "Android Studio", color: 'bg-black text-white', icon: <i class="devicon-android-plain text-2xl mr-2"/> },
        ]
    },
    {
        category: "Database Language & Frameworks", color:'bg-gradient-to-t  from-red-900 via-red-700 to-red-900',  data: [
            { item: "mongodb", color: 'bg-black text-white', icon: <i class="devicon-mongodb-plain text-2xl mr-2" /> },
            { item: "MySql", color: 'bg-black text-white', icon: <i class="devicon-mysql-plain text-2xl mr-2" /> },
            { item: "PostgreSql", color: 'bg-black text-white', icon: <i class="devicon-postgresql-plain text-2xl mr-2" /> },
            { item: "firebase", color: 'bg-black text-white', icon: <i class="devicon-firebase-plain text-2xl mr-2" /> },
        ]
    }
];


function Skills() {
    return (
        <Box component={Grid} container overflow='auto' alignSelf='center' width='60rem' backgroundColor='red' overflow='auto' spacing={1}>
            <Grid item width={5/8}>
                <Paper sx={{backgroundColor:'transparent'}}>
                    <Typography mx='auto' fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' borderRadius='0.5rem' 
                    p={1} color={indigo[900]} mb={2}> 
                        Hard Skills
                    </Typography>

                    <Stack spacing={1}>
                        {hardData.map(data =>

                            <Stack>
                                <Paper sx={data.sx}>
                                    <Typography textAlign='center' color='white' fontSize='1.2rem' fontWeight='600'>{data.category}</Typography>
                                    <Stack direction='row' spacing={1} p={1} flexWrap='wrap'>
                                        {data.data.map(item => 
                                             <Chip  color='primary' sx={{ fontSize: '1rem' , color:'white', borderColor:'black', backgroundColor:'black' }}
                                             icon={<Icon  />}
                                             label={item.item} variant='outlined' />
                                            )}
                                    </Stack>
                                </Paper>
                            </Stack>

                            )}
                    </Stack>
                </Paper>
            </Grid>
            <Grid item width={3/8}>
            <Typography mx='auto' fontSize='1.7rem' fontWeight='600' backgroundColor='white' width='fit-content' borderRadius='0.5rem' p={1} color={pink[900]}> 
                        Soft Skills
                    </Typography>
            </Grid>
        </Box>
    )
}

export default Skills;
